import React, {useState} from 'react';
import { useNavigate } from 'react-router-dom';

const Login = ({ onLogin }) => {
 const [email, setEmail] = useState('');
 const [password, setpassword] = useState('');
 const [error, setError] = useState('');
 const Navigate = useNavigate();

 const loginAction = () =>{
    const hardcodedemail = 'test@example.com';
    const hardcodedpwd = 'pass1234';

    if(email === hardcodedemail && password === hardcodedpwd){
        onLogin(email);
        Navigate("/welcome");
    }
    else{
        setError("Username or Password is incorrect");
    }
 };

 return(
 <div>
    <h2>Sign in With</h2>
    <input type = "email" value={email} onChange={(e) => setEmail(e.target.value)} required/>
    <input type = "password" value={password} onChange={(e) => setpassword(e.target.value)} required/>
    {error && <div style = {{color: 'red'}}>{error}</div>}
    <button onClick={loginAction}>Sign In</button>
    <button onClick={() => window.location.href = 'https://facebook.com'}>Facebook</button>
    <button onClick={() => window.location.href = 'https://gmail.com'}>Google</button>
    <div>Forgot Password</div>
    <div>Signup now</div>
 </div>

 );




}
